// Auth.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Auth.css';

const Auth = ({ type }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Updated to use useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const apiUrl = type === 'login'
      ? 'http://localhost:5550/api/auth/login'
      : 'http://localhost:5550/api/auth/signup';

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const data = await response.json();

      if (response.ok) {
        // Check for successful response messages
        if (type === 'login' && data.message === 'success') {
          localStorage.setItem('token', data.token);
          localStorage.setItem('user', JSON.stringify(data.user));
          navigate('/home'); // Redirect to home after login
        } else if (type === 'signup' && data.message === 'User registered successfully') {
          navigate('/login'); // Redirect to login page after successful signup
        }
      } else {
        setError(data.message || 'Authentication failed. Please try again.');
      }
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };


  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit}>
        <h2>{type === 'login' ? 'Login' : 'Sign Up'}</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        {error && <p className="error">{error}</p>}
        <button type="submit">{type === 'login' ? 'Login' : 'Sign Up'}</button>
      </form>
    </div>
  );
};

export default Auth;
