import React, { useState, useEffect } from 'react';
import '../Room.css';

const RoomManagement = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [rooms, setRooms] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const response = await fetch('http://localhost:5550/api/rooms');
      const data = await response.json();
      setRooms(data);
    } catch (err) {
      setError('Could not fetch rooms. Please try again later.');
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:5550/api/rooms', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, description })
      });

      if (response.ok) {
        fetchRooms(); // Refresh the list of rooms after creation
        setTitle('');
        setDescription('');
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to create room. Try again.');
      }
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };

  return (
    <div className="room-container">
      <h2>Create a Room</h2>
      <form onSubmit={handleCreateRoom}>
        <input
          type="text"
          placeholder="Room Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Room Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
        {error && <p className="error">{error}</p>}
        <button type="submit">Create Room</button>
      </form>

      <h2>Rooms</h2>
      <div className="room-list">
        {rooms.map((room) => (
          <div key={room._id} className="room-item">
            <h3>{room.title}</h3>
            <p>{room.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoomManagement;
